#include "p.h"

int	main(void)
{
	char	*grid;
	int		new_grid[400];
	int		i;
	int		j;
	double	prod;
	double	res;

	grid = "";
	i = -1;
	while (++i < 20)
	{
		j = -1;
		while (++j < 20)
			new_grid[i * 20 + j] = (grid[i * 40 + j * 2] - 48) * 10 + grid[i * 40 + j * 2 + 1] - 48;
	}
	res = 0;
	i = -1;
	while (++i < 20)
	{
		j = -1;
		while (++j < 17)
		{
			prod = 1;
			prod = new_grid[i * 20 + j] * new_grid[i * 20 + j + 1] * new_grid[i * 20 + j + 2] * new_grid[i * 20 + j + 3];
			if (prod > res)
				res = prod;
		}
	}
	i = -1;
	while (++i < 17)
	{
		j = -1;
		while (++j < 20)
		{
			prod = 1;
			prod = new_grid[i * 20 + j] * new_grid[(i + 1) * 20 + j] * new_grid[(i + 2) * 20 + j] * new_grid[(i + 3) * 20 + j];
			if (prod > res)
				res = prod;
		}
	}
	i = -1;
	while (++i < 17)
	{
		j = -1;
		while (++j < 17)
		{
			prod = 1;
			prod = new_grid[i * 20 + j] * new_grid[(i + 1) * 20 + j + 1] * new_grid[(i + 2) * 20 + j + 2] * new_grid[(i + 3) * 20 + j + 3];
			if (prod > res)
				res = prod;
		}
	}
	i = -1;
	while (++i < 17)
	{
		j = 2;
		while (++j < 20)
		{
			prod = 1;
			prod = new_grid[i * 20 + j] * new_grid[(i + 1) * 20 + j - 1] * new_grid[(i + 2) * 20 + j - 2] * new_grid[(i + 3) * 20 + j - 3];
			if (prod > res)
				res = prod;
		}
	}
	printf("prod = %f\n", res);
	return (0);
}
